import React from "react";
import styled from "styled-components";
import { CleanButton } from "./Buttons";

export const FilterButtonLine = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  gap: 40px;
  width: 100%;
  margin-bottom: 40px;
  font-size: 18px;
`;

export const FilterRengeLine = styled(FilterButtonLine)`
  gap: 47.5px;
  height: 60px;
  background-color: #fafafa;
`;
interface IFilterButtom {
  text: string;
}
const FilterButtom: React.FC<IFilterButtom> = (props) => {
  return (
    <CleanButton>
      {props.text} <img src="/imges/icon/downArrow.svg" alt="downArrow" />
    </CleanButton>
  );
};

export default FilterButtom;
