import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import Hero from "./Hero/Hero";
import CardsContiner from "./CardContiner/CardContiner";
import IconBooks from "./IconsBooks/IconsBooks";
import CardResturant from "../../Cards/CardResturant/CardResturant";
import CardDish from "../../Cards/CardDish/CardDish";
import FoodType from "../../Types/enums/FoodType";
import ChefOfTheWeek from "./ChafOfTheWeek/ChafOfTheWeek";
import About from "./About/About";
import { RootState } from "../../../data/store";
import { topThreePopularRestaurants } from "../../../data/resturantSlicer";
import IRestaurant from "../../Types/Interfaces/IRestaurant";
import data from "../../../data/resturants.json";
import { useNavigate } from "react-router-dom";

const demoImg =
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTjHm18IGPgsY84_ob-qzQ7Z3OcCZOmtBoUKA&usqp=CAU";
const demoFoodImg =
  "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSj9LB5LddQtJaOcSTPcyxsbc2KwcwDYqwpaQ&usqp=CAU";
const HomePage = () => {
  const navigation: any = useNavigate();
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(topThreePopularRestaurants());
  }, []);
  const restaurants = useSelector(
    (state: RootState) => state.restaurants.value
  );
  return (
    <div>
      <Hero />
      <CardsContiner
        title={"popular restaurant in epicure:"}
        cards={
          <>
            {restaurants.map((restaurant: IRestaurant, index: number) => {
              let chef = data.chef.find(
                (chef) => chef._id === restaurant.chefId
              );
              let name: string = chef?.firstName + " " + chef?.lastName;
              return (
                <CardResturant
                  key={index}
                  resturantData={restaurant}
                  chef={name}
                />
              );
            })}
          </>
        }
        isToAllButton={true}
        buttonText="All restaurants"
        buttonFunc={() => {
          navigation("/resturants");
        }}
      />
      <CardsContiner
        title={"SIGNATURE DISH OF:"}
        cards={
          <>
            <CardDish
              img={demoFoodImg}
              name={"to good to be true"}
              foodType={[FoodType.spicy]}
              ingredients={["yumy", "meet"]}
              price={54}
            />
            <CardDish
              img={""}
              name={""}
              foodType={[]}
              ingredients={[]}
              price={0}
            />
            <CardDish
              img={""}
              name={""}
              foodType={[]}
              ingredients={[]}
              price={0}
            />
          </>
        }
      />
      <IconBooks />
      <ChefOfTheWeek />
      <About />
    </div>
  );
};
export default HomePage;
