import { createSlice } from "@reduxjs/toolkit";
import { IChef } from "../component/Types/Interfaces/IChef";
import data from "./resturants.json";
const yearInMilliseconds = 31536000000;
export interface IChefState {
  chefsData: IChef[];
  value: IChef[];
  chefName: string;
  singleChef: IChef;
}
const getDishData = async () => {
  return await fetch("http://localhost:8000/restaurants")
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
      return data.data;
    })
    .catch((error) => {
      console.error("Error adding user:", error);
    });
};
const restaurantsData: IChef[] = await getDishData();

export const chefSlice = createSlice({
  name: "chef",
  initialState: {
    value: [] as IChef[],
    chefName: "",
    singleChef: {},
  } as IChefState,
  reducers: {
    allChefs: (state) => {
      state.value = restaurantsData;
    },
    popularChefs: (state) => {
      state.value = restaurantsData.sort((a, b) => a.visited - b.visited);
    },
    newChefs: (state) => {
      const currentDate = new Date().getTime();
      state.value = restaurantsData.filter((e) => {
        return (
          new Date(e.startDate).getTime() > currentDate - yearInMilliseconds * 5
        );
      });
    },
    getChefName: (state, action) => {
      const temp = restaurantsData.find((e) => e._id === action.payload);
      state.chefName = temp?.firstName + " " + temp?.lastName;
    },
    getChefOfTheWeek: (state) => {
      const chefOfTheWeek = state.chefsData.find(
        (e) => e._id === data.chefOfTheWeek
      );
      if (chefOfTheWeek) {
        state.singleChef = chefOfTheWeek;
      }
    },
  },
});

export const {
  allChefs,
  popularChefs,
  newChefs,
  getChefName,
  getChefOfTheWeek,
} = chefSlice.actions;

export default chefSlice.reducer;
