import React, { ChangeEvent, useRef, useState } from "react";
import { useDispatch } from "react-redux";
import "./orderCardDish.css";
const OrderCardDish: React.FC = (props) => {
  const demoOption = ["White bread", "Less spicy", "Less spicy", "Less spicy"];
  const optionPresented = demoOption.join(" | ");
  return (
    <div className="orderCardDish">
      <img src="/imges/clarodesktop.png" alt="img" />
      <div className="mainCardPart">
        <div>
          <div className="amount">1</div>
          <div className="nameAndPrice">
            <span className="dishName">a</span>
            <span className="dishPrice">₪88</span>
          </div>
        </div>
        <div className="dishOption">{optionPresented}</div>
        <span className="bigDishPrice">₪88</span>
      </div>
    </div>
  );
};
export default OrderCardDish;
