import React, { ReactElement, useState } from "react";
import { Outlet } from "react-router-dom";
import FilterButtom, {
  CleanButton as FilterButton,
} from "../../Styled_Components/Buttons";
import {
  FilterButtonLine,
  FilterRengeLine,
} from "../../Styled_Components/filterButtonLine";
import {
  GridCards,
  PageCenterStyle,
} from "../../Styled_Components/gridPagesStyles";
import data from "../../../data/resturants.json";
import CardResturant from "../../Cards/CardResturant/CardResturant";
import { useNavigate } from "react-router-dom";
const ResturantsPage = () => {
  const navigat = useNavigate();
  const [withFilter, setFilter] = useState("all");
  const getData = (state: string) => {
    setFilter(state);
  };

  return (
    <PageCenterStyle>
      <FilterButtonLine>
        <FilterButton
          name="all"
          bold={withFilter === "all"}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          All
        </FilterButton>
        <FilterButton
          name="New"
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          New
        </FilterButton>
        <FilterButton
          name="MostPopular"
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          Most Popular
        </FilterButton>
        <FilterButton
          name="OpenNow"
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          Open Now
        </FilterButton>
        <FilterButton>Map View</FilterButton>
      </FilterButtonLine>
      <FilterRengeLine>
        <FilterButtom text={"Price Range"} />
        <FilterButtom text={"Distance"} />
        <FilterButtom text={"Rating"} />
      </FilterRengeLine>
      <GridCards>
        {data.resturants.map((resturant, index) => {
          let chef = data.chef.find((chef) => chef.id === resturant.chefId);
          let name: string = chef?.firstName + " " + chef?.lastName;
          return (
            <CardResturant
              img={resturant.img}
              name={resturant.name}
              chef={name}
              rating={resturant.rating}
            />
          );
        })}
      </GridCards>
    </PageCenterStyle>
  );
};
export default ResturantsPage;
