import React, { ChangeEvent, useRef, useState } from "react";
import { CleanButton, IconDisplay } from "../../Styled_Components/Buttons";
import {
  BackGroundWindows,
  CloseButtonPopUp,
  DisplayPopUpWindow,
  InfoDisplayPopUpWindow,
} from "../../Styled_Components/PopUpWindows";
import { IDishCard } from "../../Types/Interfaces/IDishCard";
import "./CardDish.css";
const CardDish: React.FC<IDishCard> = (props) => {
  const fontName: number = props.isSmall ? 24 : 30;
  const paddingIngredient: number = props.isSmall ? 25 : 32;
  const [isOpen, setIsOpen] = useState(false);
  const [selectedSides, setSelectedOption] = useState("");
  const [changes, setChanges] = useState<any[]>([]);
  const modalRef = useRef<HTMLDivElement>(null);
  const handleOpen = () => setIsOpen(true);
  const handleClose = () => setIsOpen(false);
  const foodTypeIcons = props.dishData.foodType.map((food) => (
    <IconDisplay iconUrl={"/imges/icons/foodTypes/" + food + ".svg"} />
  ));
  const handleClickOutside = (event: React.MouseEvent) => {
    modalRef.current === event.target && handleClose();
  };
  const handleChanges = (event: any) => {
    const option = event.target.value;
    const isChecked = event.target.checked;
    if (isChecked) {
      setChanges([...changes, option]);
    } else {
      setChanges(changes.filter((changes) => changes !== option));
    }
  };
  const handleSidesChange = (event: any) => {
    setSelectedOption(event.target.value);
  };
  return (
    <>
      <div
        className={props.isSmall ? "cardDish small" : "cardDish"}
        onClick={handleOpen}
      >
        <img
          className="dishImg"
          src={props.dishData.img}
          alt={props.dishData.name}
        />
        <h3 style={{ fontSize: fontName, fontFamily: "HelveticaNeueMedium" }}>
          {props.dishData.name}
        </h3>
        {!props.isSmall && (
          <>
            <div className="foodTypes">{<></>}</div>
          </>
        )}
        <p
          style={{
            paddingLeft: paddingIngredient,
            paddingRight: paddingIngredient,
          }}
        >
          {props.ingredients.join(",")}
        </p>
        <div className="price">
          <hr />
          <span>{props.price}</span>
          <hr />
        </div>
      </div>
      {isOpen && (
        <BackGroundWindows onClick={handleClickOutside} ref={modalRef}>
          <DisplayPopUpWindow>
            <CloseButtonPopUp
              style={{ top: "-29px", left: 0 }}
              onClick={handleClose}
            />
            <img className="dishDetailImg" src={props.img} alt={props.name} />
            <InfoDisplayPopUpWindow>
              <div className="detailDish">
                <h3> {props.name}</h3>
                <p>{props.ingredients.join(",")}</p>
                <div className="foodTypes foodTypesDetailWindows">
                  {foodTypeIcons}
                </div>
                <div className="price priceNotAbsolute">
                  <hr />
                  <span>{props.price}</span>
                  <hr />
                </div>
              </div>
              {/* sides, radio buttons */}
              <div className="choosingBlock">
                <span className="underlineTitle">Choose a side</span>
                <label>
                  <input
                    type="radio"
                    value="white bread"
                    checked={selectedSides === "white bread"}
                    onChange={handleSidesChange}
                  />
                  White bread
                </label>
                <label>
                  <input
                    type="radio"
                    value="sticky rice"
                    checked={selectedSides === "sticky rice"}
                    onChange={handleSidesChange}
                  />
                  Sticky rice
                </label>
              </div>
              {/* changes , cheack box */}
              <div className="choosingBlock">
                <span className="underlineTitle">Changes</span>
                <label>
                  <input
                    type="checkbox"
                    value="Whithout peanuts"
                    checked={changes.includes("Whithout peanuts")}
                    onChange={handleChanges}
                  />
                  Whithout peanuts
                </label>
                <label>
                  <input
                    type="checkbox"
                    value="Sticky Less spicy"
                    checked={changes.includes("Sticky Less spicy")}
                    onChange={handleChanges}
                  />
                  Sticky Less spicy
                </label>
              </div>
              <div className="choosingBlock">
                <span className="underlineTitle">Quantity</span>
                <div className="Quantity">
                  <CleanButton>
                    <img src="/imges/icons/substract.svg" alt="substract" />
                  </CleanButton>
                  <span>1</span>
                  <CleanButton>
                    <img src="/imges/icons/add.svg" alt="add" />
                  </CleanButton>
                </div>
              </div>
              <button>Add to bag</button>
            </InfoDisplayPopUpWindow>
          </DisplayPopUpWindow>
        </BackGroundWindows>
      )}
    </>
  );
};
export default CardDish;
