import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { RootState } from "../../../data/store";
import OrderCardDish from "../../Cards/CardDish/orderCardDish/orderCardDish";
import { MainButton, SecondaryButton } from "../../Styled_Components/Buttons";
import "./BagPopUp.css";
const BagPopUp = () => {
  const [hasOrder, setHasOrder] = useState<boolean>(false);
  useEffect(() => {
    console.log(orders.length);
    console.log(hasOrder);
    if (orders.length > 0) {
      setHasOrder(true);
    }
  }, []);
  const orders = useSelector((state: RootState) => state.order.value);
  console.log(orders);

  return (
    <div className="BagPopUp">
      {hasOrder ? (
        <img
          src="/imges/emptyBagMesseg.svg"
          alt="emptyBag"
          className="emptyBag"
        />
      ) : (
        <div className="orderBody">
          <h3>YOUR ORDER</h3>
          <h4>Mashya</h4>
          <div className="dishCardContinuer">
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
            <OrderCardDish />
          </div>
          <div className="priceOrder">
            <hr />
            <span>₪{44}</span>
            <hr />
          </div>
          <span>Add A Comment</span>
          <textarea
            className="p"
            placeholder="Special requests, allergies, detary restrictions, etc."
          />
        </div>
      )}
      <MainButton isActive={true}>Checkout</MainButton>
      <SecondaryButton isActive={true}>Order history</SecondaryButton>
    </div>
  );
};

export default BagPopUp;
