import axios from "axios";
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { userLoggedIn } from "../../../data/orderSlicer";
import InputFiled from "../../inputFiled/inputFiled";
import {
  CleanButton,
  MainButton,
  SecondaryButton,
} from "../../Styled_Components/Buttons";
import "./SingInPage.css";
const SingINPage = () => {
  const dispatch = useDispatch();
  const navigation: any = useNavigate();
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const CanLogIn: boolean = email.length > 5 && password.length > 8;
  const handleLogIn = async (event: React.MouseEvent<HTMLElement>) => {
    try {
      const userReq = await axios.post("http://localhost:8000/users/login", {
        email: email,
        password: password,
      });
      dispatch(userLoggedIn(userReq.data));
      navigation("/");
      alert("welcome back: " + userReq.data.first_name);
    } catch (error: any) {
      alert(error.response.data);
      return [];
    }
  };

  return (
    <div className="main">
      <div className="singINCard">
        <div className="info">
          <span>SIGN IN</span>
          <p>To continue the order, please sign in</p>
        </div>
        <div className="inputField">
          <InputFiled
            label={"Email address"}
            onChange={(value) => setEmail(value)}
          />
          <InputFiled
            type="password"
            label={"Password"}
            onChange={(value) => setPassword(value)}
          />
        </div>
        <div className="logInForgetPassword">
          <MainButton
            isActive={CanLogIn}
            className="singUpButtons"
            onClick={handleLogIn}
          >
            login
          </MainButton>
          <CleanButton className="forgetPass">Forget password?</CleanButton>
        </div>
        <div className="orDiv">
          <hr />
          <span>or</span>
          <hr />
        </div>
        <SecondaryButton
          isActive={true}
          onClick={() => {
            navigation("/singUp");
          }}
          className="singUpButtons"
        >
          sign up
        </SecondaryButton>
      </div>
    </div>
  );
};
export default SingINPage;
