import React, { useRef, useState } from "react";
import { IconDisplay } from "../../Styled_Components/Buttons";
import IDishCard from "../../Types/Interfaces/IDishCard";
import "./CardDish.css";
const CardDish: React.FC<IDishCard> = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const modalRef = useRef<HTMLDivElement>(null);
  const handleOpen = () => setIsOpen(true);
  const handleClose = () => setIsOpen(false);
  const handleClickOutside = (event: React.MouseEvent) => {
    console.log(event.target);
    console.log(modalRef.current);
    console.log(isOpen);
    if (modalRef.current === event.target) {
      console.log("a");
      handleClose();
    }
  };
  const foodTypeIcons = props.foodType.map((food) => (
    <IconDisplay iconUrl={"/imges/icons/foodTypes/" + food + ".svg"} />
  ));
  const fontName: number = props.isSmall ? 24 : 30;
  const paddingIngredient: number = props.isSmall ? 25 : 32;
  return (
    <>
      <div
        className={props.isSmall ? "cardDish small" : "cardDish"}
        onClick={handleOpen}
      >
        <img className="dishImg" src={props.img} alt={props.name} />
        <h3 style={{ fontSize: fontName, fontFamily: "HelveticaNeueMedium" }}>
          {props.name}
        </h3>
        {!props.isSmall && (
          <>
            <div className="foodTypes">{foodTypeIcons}</div>
          </>
        )}
        <p
          style={{
            paddingLeft: paddingIngredient,
            paddingRight: paddingIngredient,
          }}
        >
          {props.ingredients.join(",")}
        </p>
        <div className="price">
          <hr />
          <span>{props.price}</span>
          <hr />
        </div>
      </div>
      {isOpen && (
        <div
          onClick={handleClickOutside}
          ref={modalRef}
          style={{
            zIndex: 50,
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0, 0, 0, 0.5)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <div
            style={{
              background: "white",
              padding: "1rem",
              border: "1px solid black",
            }}
          >
            <button onClick={handleClose}>Close</button>
          </div>
        </div>
      )}
    </>
  );
};
export default CardDish;
