import React from "react";
import ChefCard from "../../../Cards/ChefCard/ChefCard";
import "./ChefOfTheWeek.css";
import data from "../../../../data/resturants.json";
import CardResturant from "../../../Cards/CardResturant/CardResturant";
let chef = data.chef.find((i) => i.id === data.chefOfTheWeek) || data.chef[0];
let chefResturants =
  data.resturants.filter((i) => i.chefId === data.chefOfTheWeek) || [];
let firstName: string = chef.firstName;
let lastName: string = chef.lastName;
let img: string = chef.img;
let abute: string = chef.aboutMe;
interface IChefOfTheWeek {
  img: string;
  firstName: string;
  lastName: string;
  abutMe: string;
}
const ChefOfTheWeek: React.FC = () => {
  return (
    <div className="ChefOfTheWeek">
      <h2 className="titleCompo">Chef of the week:</h2>
      <div className="chefAndAboutMe">
        <ChefCard img={img} firstName={firstName} lastName={lastName} />
        <p>{abute}</p>
      </div>
      <h2>{firstName + "'s Restaurants"}</h2>
      <div className="resturantRow">
        {chefResturants.map((resturant) => (
          <CardResturant
            img={resturant.img}
            name={resturant.name}
            chef={resturant.name}
            rating={0}
            isChafOfTheWeek={true}
          />
        ))}
      </div>
    </div>
  );
};

export default ChefOfTheWeek;
