import { createSlice } from "@reduxjs/toolkit";
import { IDish, IDishOrder } from "../component/Types/Interfaces/IDishCard";
export interface IOrderState {
  value: IDishOrder[];
}
export const orderSlice = createSlice({
  name: "order",
  initialState: {
    value: [],
  },
  reducers: {
    addDishToOrder: (
      state: { value: IDishOrder[] },
      action: { payload: IDishOrder }
    ) => {
      console.log(state.value.length === 0);

      if (
        state.value.every(
          (dish) => dish.restaurantId === action.payload.restaurantId
        ) ||
        state.value.length === 0
      ) {
        let copiedDish = state.value.findIndex(
          (dish) => dish._id === action.payload._id
        );
        copiedDish
          ? (state.value[copiedDish].amount += action.payload.amount)
          : state.value.push(action.payload);
      } else {
        throw Error("not the same restaurant");
      }
    },
    cleanOrder: (state: { value: IDish[] }) => {
      state.value = [];
    },
  },
});

export const { addDishToOrder, cleanOrder } = orderSlice.actions;

export default orderSlice.reducer;
