import React from "react";
import IRestaurantCard from "../../Types/Interfaces/IRestaurantCard";
import "./CardResturant.css";
const CardResturant: React.FC<IRestaurantCard> = (props) => {
  console.log(props.isChafOfTheWeek == true);
  let rating = Math.round(props.rating);
  if (rating < 1) {
    rating = 1;
  } else if (rating > 5) {
    rating = 5;
  }
  if (props.isChafOfTheWeek) {
    return (
      <div className="cardResturant cardResturantSimplify">
        <img
          className="resturantImgSimplify"
          src={props.img}
          alt={props.name}
        />
        <h3>{props.name}</h3>
      </div>
    );
  }
  return (
    <div
      className={
        props.isChafOfTheWeek
          ? "cardResturant cardResturantSimplify"
          : "cardResturant"
      }
    >
      <img
        className={
          props.isChafOfTheWeek
            ? "resturantImg resturantImgSimplify"
            : "resturantImg"
        }
        src={props.img}
        alt={props.name}
      />
      <h3>{props.name}</h3>
      <span>{props.chef}</span>
      <img
        src={"/imges/starRating/rate-" + rating + ".svg"}
        alt={"rating" + rating}
        className="rating"
      />
    </div>
  );
};

export default CardResturant;

