import { createSlice } from "@reduxjs/toolkit";
import { IChef } from "../component/Types/Interfaces/IChef";
const yearInMilliseconds = 31536000000;
export interface IChefState {
  value: IChef[];
  chefName: string;
  singleChef: IChef;
}
const getChefsData = async () => {
  return await fetch("http://localhost:8000/chefs")
    .then((response) => response.json())
    .then((data) => {
      console.log(data);
      return data.data;
    })
    .catch((error) => {
      console.error("Error getting chefs data:", error);
    });
};
const chefsData: IChef[] = await getChefsData();
const IDchefOfTheWeek = "6408b12b18f482cbda0155d6";
export const chefSlice = createSlice({
  name: "chef",
  initialState: {
    value: [] as IChef[],
    chefName: "",
    singleChef: {},
  } as IChefState,
  reducers: {
    allChefs: (state) => {
      state.value = chefsData;
    },
    popularChefs: (state) => {
      state.value = [...chefsData].sort((a, b) => a.visited - b.visited);
    },
    newChefs: (state) => {
      const currentDate = new Date().getTime();
      state.value = chefsData.filter((e) => {
        return (
          new Date(e.startDate).getTime() > currentDate - yearInMilliseconds * 5
        );
      });
    },
    getChefName: (state, action) => {
      const temp = chefsData.find((e) => e._id === action.payload);
      state.chefName = temp?.firstName + " " + temp?.lastName;
    },
    getChefOfTheWeek: (state) => {
      const chefOfTheWeek = chefsData.find((e) => e._id === IDchefOfTheWeek);
      if (chefOfTheWeek) {
        state.singleChef = chefOfTheWeek;
      }
    },
  },
});

export const {
  allChefs,
  popularChefs,
  newChefs,
  getChefName,
  getChefOfTheWeek,
} = chefSlice.actions;

export default chefSlice.reducer;
