import React, { useEffect, useRef } from "react";
function ExUseRef(props: any): JSX.Element {
  const count = useRef(0);

  useEffect(() => {
    count.current = count.current + 1;
  });

  return (
    <div>
      <span>get renders count {count.current}</span>
    </div>
  );
}
export default ExUseRef;
