import {
  AnyAction,
  createSlice,
  Dispatch,
  PayloadAction,
} from "@reduxjs/toolkit";
import { IDish } from "../component/Types/Interfaces/IDishCard";
import data from "./resturants.json";
import { DayPart } from "../component/Types/enums/DishEnums";
import axios from "axios";

export interface IDishesState {
  value: IDish[];
}

const filterDishesByDayPart = async (
  restaurantId: string,
  dayPart: DayPart
) => {
  try {
    const { data } = await axios.post("http://localhost:8000/dishes", {
      resturantId: restaurantId,
      DayPart: dayPart,
    });
    return data;
  } catch (error) {
    console.error(error);
    return [];
  }
};

export const fetchDishesByDayPart =
  (restaurantId: string, dayPart: DayPart) =>
  async (dispatch: Dispatch<AnyAction>) => {
    console.log(restaurantId, dayPart);
    const dishes: IDish[] = await filterDishesByDayPart(restaurantId, dayPart);
    dispatch(setDishes(dishes));
  };
export const dishSlice = createSlice({
  name: "dishes",
  initialState: {
    dishesData: data.dishes,
    value: [] as IDish[],
  },
  reducers: {
    setDishes: (state, action: PayloadAction<IDish[]>) => {
      state.value = action.payload;
    },
    topThreePopularDish: (state) => {
      // state.value = state.dishesData
      //   .sort((a, b) => a.visited - b.visited)
      //   .slice(0, 3);
    },
  },
});

export const { setDishes, topThreePopularDish } = dishSlice.actions;

export default dishSlice.reducer;
