import { createSlice } from "@reduxjs/toolkit";
import { IChef } from "../component/Types/Interfaces/IChef";
import data from "./resturants.json";
const yearInMilliseconds = 31536000000;
export interface IChefState {
  chefsData: IChef[];
  value: IChef[];
  chefName: string;
  singleChef: IChef;
}
export const chefSlice = createSlice({
  name: "chef",
  initialState: {
    chefsData: data.chef,
    value: data.chef,
    chefName: "",
    singleChef: {},
  } as IChefState,
  reducers: {
    allChefs: (state) => {
      state.value = state.chefsData;
    },
    popularChefs: (state) => {
      state.value = state.chefsData.sort((a, b) => a.visited - b.visited);
    },
    newChefs: (state) => {
      const currentDate = new Date().getTime();
      state.value = state.chefsData.filter((e) => {
        return (
          new Date(e.startDate).getTime() > currentDate - yearInMilliseconds * 5
        );
      });
    },
    getChefName: (state, action) => {
      const temp = state.chefsData.find((e) => e._id === action.payload);
      state.chefName = temp?.firstName + " " + temp?.lastName;
    },
    getChefOfTheWeek: (state) => {
      state.singleChef = state.chefsData.find(
        (e) => e._id === data.chefOfTheWeek
      );
    },
  },
});

export const {
  allChefs,
  popularChefs,
  newChefs,
  getChefName,
  getChefOfTheWeek,
} = chefSlice.actions;

export default chefSlice.reducer;
