import React, { useRef, useState } from "react";
import { NavLink, useNavigate, useNavigation } from "react-router-dom";
import BagPopUp from "../PopUps/BagPopUp/BagPopUp";
import {
  CleanButtonWithAnimation,
  IconButton,
} from "../Styled_Components/Buttons";
import "./Header.css";
const Header: React.FC = () => {
  const navigation: any = useNavigate();
  const [openBag, setOpenBag] = useState<boolean>(false);
  const modalRef = useRef<HTMLDivElement>(null);
  const handleClickOutside = (event: React.MouseEvent) => {
    modalRef.current === event.target && setOpenBag(false);
  };
  return (
    <header>
      <div className="headerFrame">
        <nav>
          <div
            className="logoDiv"
            onClick={() => {
              navigation("/");
            }}
          >
            <img src="/imges/icons/logo.svg" alt="logo" />
            <p>EPICURE</p>
          </div>
          <NavLink
            to="/resturants"
            className={({ isActive }) => (isActive ? "undeline" : "notActive")}
          >
            Restaurants
          </NavLink>
          <NavLink
            to="/chefs"
            className={({ isActive }) => (isActive ? "undeline" : "notActive")}
          >
            Chefs
          </NavLink>
        </nav>
        <nav className="menu-right">
          <input type="text" />
          <IconButton
            onClick={() => setOpenBag(!openBag)}
            iconUrl="/imges/icons/bag.svg"
          />
          <IconButton
            onClick={() => {
              navigation("/singIn");
            }}
            iconUrl="/imges/icons/person.svg"
          />
        </nav>
      </div>
      {openBag && (
        <BagPopUp forwardRef={modalRef} onClick={handleClickOutside} />
      )}
    </header>
  );
};
export default Header;
