import React from "react";
import { IconDisplay } from "../../Styled_Components/Buttons";
import IDishCard from "../../Types/Interfaces/IDishCard";
import "./CardDish.css";
const CardDish: React.FC<IDishCard> = (props) => {
  const foodTypeIcons = props.foodType.map((food) => (
    <IconDisplay iconUrl={"/imges/icons/foodTypes/" + food + ".svg"} />
  ));
  const fontName: number = props.isSmall ? 24 : 30;
  return (
    <div className={props.isSmall ? "cardDish small" : "cardDish"}>
      <img className="dishImg" src={props.img} alt={props.name} />
      <h3 style={{ fontSize: fontName }}>{props.name}</h3>
      {!props.isSmall && (
        <>
          <div className="foodTypes">{foodTypeIcons}</div>
        </>
      )}
      <div className="price">
        <p>{props.ingredients.join(",")}</p>
        <hr />
        <span>
          <img src="/imges/icons/shekel.svg" alt="shekel" />
          {props.price}
        </span>
        <hr />
      </div>
    </div>
  );
};
export default CardDish;
