import { SwitchButton } from "../../Styled_Components/Buttons";
import { FilterButtonLine } from "../../Styled_Components/filterButtonLine";
import {
  GridCards,
  PageCenterStyle,
} from "../../Styled_Components/gridPagesStyles";
import data from "../../../data/resturants.json";
import ChefCard from "../../Cards/ChefCard/ChefCard";
import { useState } from "react";
const ChafPage = () => {
  const [withFilter, setFilter] = useState("all");
  const getData = (state: string) => {
    setFilter(state);
  };
  return (
    <PageCenterStyle>
      <FilterButtonLine>
        <SwitchButton
          name="all"
          bold={withFilter === "all"}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          All
        </SwitchButton>
        <SwitchButton
          name="New"
          bold={withFilter === "New"}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          New
        </SwitchButton>
        <SwitchButton
          name="MostViewed"
          bold={withFilter === "MostViewed"}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          Most Viewed
        </SwitchButton>
      </FilterButtonLine>
      <GridCards repastCell={3}>
        {data.chef.map((chef) => {
          return <ChefCard chefData={chef} />;
        })}
      </GridCards>
    </PageCenterStyle>
  );
};
export default ChafPage;
