import React from "react";
import { Outlet } from "react-router-dom";
import "./ResturantsPage.css";
import FilterButtom, { CleanButton } from "../../Styled_Components/Buttons";
import {
  FilterButtonLine,
  FilterRengeLine,
} from "../../Styled_Components/filterButtonLine";
import { GridCards } from "../../Styled_Components/gridCard";
import data from "../../../data/resturants.json";
import CardResturant from "../../Cards/CardResturant/CardResturant";
const ResturantsPage = () => {
  return (
    <div className="resturantsPage">
      <FilterButtonLine>
        <CleanButton>All</CleanButton>
        <CleanButton>New</CleanButton>
        <CleanButton>Most Popular</CleanButton>
        <CleanButton>Open Now</CleanButton>
        <CleanButton>Map View</CleanButton>
      </FilterButtonLine>
      <FilterRengeLine>
        <FilterButtom text={"Price Range"} />
        <FilterButtom text={"Distance"} />
        <FilterButtom text={"Rating"} />
      </FilterRengeLine>
      <GridCards>
        {data.resturants.map((resturant) => {
          let chef = data.chef.find((chef) => chef.id === resturant.chefId);
          let name: string = chef?.firstName + " " + chef?.lastName;
          return (
            <CardResturant
              img={resturant.img}
              name={resturant.name}
              chef={name}
              rating={resturant.rating}
            />
          );
        })}
      </GridCards>
    </div>
  );
};
export default ResturantsPage;
