import styled from "styled-components";
interface IStyleIcon {
  iconUrl: string;
  width?: number;
  height?: number;
}
interface IFilterButton {
  bold: boolean;
}
export const MainButton = styled.button`
  background: black;
  color: white;
  padding: 14px 36.5px;
  font-weight: 400;
  font-size: 18px;
  &:hover {
    background-color: #515151;
  }
`;
export const CleanButton = styled.button`
  background: none;
  color: inherit;
  border: none;
  padding: 0;
  font: inherit;
  cursor: pointer;
  outline: inherit;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  height: 100%;
  gap: 11px;
`;
export const SwitchButton = styled(CleanButton)<IFilterButton>`
  font-weight: ${(props) => (props.bold ? "bold" : "normal")};
`;
export const CleanButtonWithAnimation = styled(CleanButton)`
  border: 0;
  height: fit-content;
  &:hover {
    padding-bottom: 8px;
    border-bottom: 1.8px solid rgba(222, 146, 0, 0.9);
  }
`;
export const IconButton = styled(CleanButton)<IStyleIcon>`
  width: 24px;
  height: 24px;
  background: url(${(props) => props.iconUrl}) no-repeat;
  background-size: cover;
`;
export const IconDisplay = styled(CleanButton)<IStyleIcon>`
  width: 50px;
  height: 50px;
  background: url(${(props) => props.iconUrl}) no-repeat;
  background-size: contain;
`;
export const IPupDiv = styled.div`
  position: absolute;
  top: calc(100% + 10px);
  left: 0;
  width: 200px;
  height: 200px;
  background-color: red;
`;
