import { type } from "os";
import React from "react";
import CardDish from "../../../Cards/CardDish/CardDish";
import CardResturant from "../../../Cards/CardResturant/CardResturant";
import FoodType from "../../../Types/enums/FoodType";
import "./CardContiner.css";
interface Interface {
  title: string;
  interfaceCard: "resturant" | "dish";
  isToAllButton?: boolean;
  buttonText?: string;
  buttonFunc?: Function;
}

const CardsContiner: React.FC<Interface> = (props) => {
  let cards;
  let buttonDiv;
  const isButton = props.isToAllButton || false;
  if (isButton) {
    buttonDiv = (
      <div onClick={() => props.buttonFunc} className="textButton">
        <span>{props.buttonText || "All"}</span>
        <img src="/imges/icons/errow.svg" alt="errow" />
      </div>
    );
  }
  if (props.interfaceCard === "resturant") {
    cards = (
      <>
        <CardResturant
          name="Claro"
          img="/imges/clarodesktop.png"
          chef="Ran Shmueli"
          rating={4}
        />
        <CardResturant
          name="Claro"
          img="/imges/clarodesktop.png"
          chef="Ran Shmueli"
          rating={4}
        />
        <CardResturant
          name="Claro"
          img="/imges/clarodesktop.png"
          chef="Ran Shmueli"
          rating={4}
        />
      </>
    );
  } else if (props.interfaceCard === "dish") {
    cards = (
      <>
        <CardDish
          img={"/imges/clarodesktop.png"}
          name={"ido"}
          foodType={[FoodType.spicy]}
          ingredients={["hsddsf", "sdfsfs"]}
          price={0}
        />
        <CardDish
          img={"/imges/clarodesktop.png"}
          name={"ido"}
          foodType={[FoodType.spicy, FoodType.vegan]}
          ingredients={["hsddsf", "sdfsfs"]}
          price={0}
        />
        <CardDish
          img={"/imges/clarodesktop.png"}
          name={"ido"}
          foodType={[FoodType.spicy]}
          ingredients={["hsddsf", "sdfsfs"]}
          price={0}
        />
      </>
    );
  }
  return (
    <div className="rowAndTitle">
      <h2>{props.title}</h2>
      <div className="row">{cards}</div>
      {buttonDiv}
    </div>
  );
};

export default CardsContiner;

