import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import InputFiled from "../../inputFiled/inputFiled";
import {
  CleanButton,
  MainButton,
  SecondaryButton,
} from "../../Styled_Components/Buttons";
import "./SingInPage.css";
const SingINPage = () => {
  const navigation: any = useNavigate();
  const [email, setEmail] = useState<string>("");
  const [password, setPassword] = useState<string>("");
  const handleSetEmail = (value: string) => setEmail(value);
  const handlesSetPassword = (value: string) => setPassword(value);
  const CanLogIn: boolean = email.length > 5 && password.length > 8;
  return (
    <div className="main">
      <div className="singINCard">
        <div className="info">
          <span>SIGN IN</span>
          <p>To continue the order, please sign in</p>
        </div>
        <div className="inputField">
          <InputFiled label={"Email address"} onChange={handleSetEmail} />
          <InputFiled
            type="password"
            label={"Password"}
            onChange={handlesSetPassword}
          />
        </div>
        <div className="logInForgetPassword">
          <MainButton isActive={CanLogIn} className="singUpButtons">
            login
          </MainButton>
          <CleanButton className="forgetPass">Forget password?</CleanButton>
        </div>
        <div className="orDiv">
          <hr />
          <span>or</span>
          <hr />
        </div>
        <SecondaryButton
          isActive={true}
          onClick={() => {
            navigation("/singUp");
          }}
          className="singUpButtons"
        >
          sign up
        </SecondaryButton>
      </div>
    </div>
  );
};
export default SingINPage;
