import React from "react";
import FilterButtom, {
  CleanButton as MyComponent,
} from "../../Styled_Components/Buttons";
import {
  FilterButtonLine,
  FilterRengeLine,
} from "../../Styled_Components/filterButtonLine";
import {
  GridCards,
  PageCenterStyle,
} from "../../Styled_Components/gridPagesStyles";
import data from "../../../data/resturants.json";
import CardResturant from "../../Cards/CardResturant/CardResturant";
const ResturantsPage = () => {
  return (
    <PageCenterStyle>
      <FilterButtonLine>
        <MyComponent>All</MyComponent>
        <MyComponent>New</MyComponent>
        <MyComponent>Most Popular</MyComponent>
        <MyComponent>Open Now</MyComponent>
        <MyComponent>Map View</MyComponent>
      </FilterButtonLine>
      <FilterRengeLine>
        <FilterButtom text={"Price Range"} />
        <FilterButtom text={"Distance"} />
        <FilterButtom text={"Rating"} />
      </FilterRengeLine>
      <GridCards>
        {data.resturants.map((resturant, index) => {
          let chef = data.chef.find((chef) => chef.id === resturant.chefId);
          let name: string = chef?.firstName + " " + chef?.lastName;
          return (
            <CardResturant
              img={resturant.img}
              name={resturant.name}
              chef={name}
              rating={resturant.rating}
            />
          );
        })}
      </GridCards>
    </PageCenterStyle>
  );
};
export default ResturantsPage;
