import { type } from "os";
import React from "react";
import { IconButton } from "../../Button/Buttons";
import IDishCard from "../../Types/Interfaces/IDishCard";
import IRestaurantCard from "../../Types/Interfaces/IRestaurantCard";
import "./CardResturant.css";

const CardResturant: React.FC<IRestaurantCard | IDishCard> = (props) => {
  let chefOrIngredients: string;
  let foodTypeIcons;
  if ("chef" in props) {
    chefOrIngredients = props.chef;
  } else {
    foodTypeIcons = props.foodType
      .map((food) => (
        <IconButton iconUrl={"/imges/icons/foodTypes/" + food + ".svg"} />
      ))
      .join("");
    chefOrIngredients = props.ingredients.join(",");
  }
  console.log(foodTypeIcons);
  return (
    <div className="cardResturant">
      <img className="resturantImg" src={props.img} alt={props.name} />
      <h3>{props.name}</h3>
      <div className="foodTypes">{foodTypeIcons}</div>
      <div>{chefOrIngredients}</div>
      {/* <img
        src={`/images/starRating/rate-${props.rating}.svg`}
        alt={`rating ${props.rating}`}
        className="rating"
      /> */}
    </div>
  );
};

export default CardResturant;
