import styled from "styled-components";

export const GridCards = styled.div`
  width: fit-content;
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(500px, 1fr));
  grid-auto-rows: auto;
  row-gap: 40px;
  column-gap: 24px;
`;

export const PageCenterStyle = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin-top: 48px;
`;
