import React, { ChangeEvent, useRef, useState } from "react";
import { useDispatch } from "react-redux";
import { addDishToOrder } from "../../../data/orderSlicer";
import PopUpDish from "../../experiment/popUpDish";
import { CleanButton, IconDisplay } from "../../Styled_Components/Buttons";
import {
  BackGroundWindows,
  CloseButtonPopUp,
  DisplayPopUpWindow,
  InfoDisplayPopUpWindow,
} from "../../Styled_Components/PopUpWindows";
import { IDish, IDishCard } from "../../Types/Interfaces/IDishCard";
import "./CardDish.css";
const CardDish: React.FC<IDishCard> = (props) => {
  const dispatch = useDispatch();
  //data
  const dishData: IDish = props.dishData;
  // if cardDish is small some style changes happened
  const fontName: number = props.isSmall ? 24 : 30;
  const paddingIngredient: number = props.isSmall ? 25 : 32;
  //handle closing and opening popup
  const [isOpen, setIsOpen] = useState<boolean>(false);
  const handleOpen = () => setIsOpen(true);
  const handleClose = () => setIsOpen(false);
  const modalRef = useRef<HTMLDivElement>(null);
  const handleClickOutside = (event: React.MouseEvent) => {
    modalRef.current === event.target && handleClose();
  };
  //dish side handle
  const [selectedSides, setSelectedOption] = useState("");
  const handleSidesChange = (event: any) => {
    setSelectedOption(event.target.value);
  };
  //dish changes handle
  const [changes, setChanges] = useState<any[]>([]);
  const handleChanges = (event: any) => {
    const option = event.target.value;
    const isChecked = event.target.checked;
    if (isChecked) {
      setChanges([...changes, option]);
    } else {
      setChanges(changes.filter((changes) => changes !== option));
    }
  };
  // handle quantity of dishes
  const [quantity, setQuantity] = useState<number>(1);
  const quantityChange = (action: string) => {
    switch (action) {
      case "+":
        setQuantity(quantity + 1);
        break;
      case "-":
        quantity > 0 && setQuantity(quantity - 1);
        break;
    }
  };
  //create food type image arry to present
  const foodTypeIcons = dishData.foodType.map((food: string) => (
    <img
      className="foodTypeLogo"
      src={"/imges/icons/foodTypes/" + food + ".svg"}
      alt={food}
    />
  ));

  return (
    <>
      <div
        className={props.isSmall ? "cardDish small" : "cardDish"}
        onClick={handleOpen}
      >
        <img className="dishImg" src={dishData.img} alt={dishData.name} />
        <h3 className="title" style={{ fontSize: fontName }}>
          {dishData.name}
        </h3>
        {!props.isSmall && <div className="foodTypes">{foodTypeIcons}</div>}
        <p
          className="ingredients"
          style={{
            paddingLeft: paddingIngredient,
            paddingRight: paddingIngredient,
          }}
        >
          {dishData.ingredients.join(",")}
        </p>
        <div className="price">
          <hr />
          <span>₪{dishData.price}</span>
          <hr />
        </div>
      </div>
      {isOpen && (
        <PopUpDish
          ref={modalRef}
          onClick={handleClickOutside}
          ref={modalRef}
          dish={dishData}
          onSubmit={() => {
            handleClose();
          }}
        />
      )}
    </>
  );
};
export default CardDish;
