import { createSlice } from "@reduxjs/toolkit";
import axios from "axios";
import { DayPart } from "../component/Types/enums/DishEnums";
import { IDish } from "../component/Types/Interfaces/IDishCard";
import data from "./resturants.json";
export interface IDishesState {
  value: IDish[];
}
const filterByDayPart = async (restaurantId: string, DayPart: DayPart) => {
  try {
    let data = await axios.post("http://localhost:8000/dishes", {
      resturantId: restaurantId,
      DayPart: DayPart,
    });
    return data.data;
  } catch (error) {
    console.error(error);
    return [];
  }
};

export const dishSlice = createSlice({
  name: "dishes",
  initialState: {
    dishesData: data.dishes,
    value: data.dishes,
  },
  reducers: {
    morning: async (state, action) => {
      // fetch("http://localhost:8000/dishes", {
      //   method: "POST",
      //   body: JSON.stringify({
      //     resturantId: "64075501752f15ccca196049",
      //     DayPart: DayPart.Breakfast,
      //   }),
      //   headers: {
      //     "Content-type": "application/json; charset=UTF-8",
      //   },
      // })
      //   .then((response) => response.json())
      //   .then((data) => {
      //     state.value = data.data;
      //   })
      //   .catch((error) => {
      //     console.error("Error adding user:", error);
      //   });
      state.value = await filterByDayPart(
        "64075501752f15ccca196049",
        DayPart.Breakfast
      );
    },
    noon: (state, action) => {
      state.value = state.dishesData.filter(
        (e) => e.DayPart === DayPart.Launch && e.resturantId === action.payload
      );
    },
    evening: (state, action) => {
      state.value = state.dishesData.filter(
        (e) => e.DayPart === DayPart.Dinner && e.resturantId === action.payload
      );
    },
    topThreePopularDish: (state) => {
      state.value = state.dishesData
        .sort((a, b) => a.visited - b.visited)
        .slice(0, 3);
    },
  },
});

export const { morning, noon, evening, topThreePopularDish } =
  dishSlice.actions;

export default dishSlice.reducer;
