import React, { ReactElement, useState } from "react";
import { Outlet } from "react-router-dom";
import {
  FilterButtonLine,
  FilterRengeLine as FilterRenegeLine,
} from "../../Styled_Components/filterButtonLine";
import {
  GridCards,
  PageCenterStyle,
} from "../../Styled_Components/gridPagesStyles";
import data from "../../../data/resturants.json";
import CardResturant from "../../Cards/CardResturant/CardResturant";
import { SwitchButton } from "../../Styled_Components/Buttons";
import FilterButtom from "../../Styled_Components/FilterButtom";
const ResturantsPage = () => {
  const [withFilter, setFilter] = useState("all");
  const [openWindow, setOpenWindow] = useState("");
  const getDataFromButtonFilter = (state: string) => {
    setFilter(state);
  };
  const OpenFiltersWindow = (state: string) => {
    setOpenWindow(state);
  };
  return (
    <PageCenterStyle>
      <FilterButtonLine>
        <SwitchButton
          name="all"
          bold={withFilter === "all"}
          onClick={(value: any) => {
            getDataFromButtonFilter(value.target.name);
          }}
        >
          All
        </SwitchButton>
        <SwitchButton
          name="New"
          bold={withFilter === "New"}
          onClick={(value: any) => {
            getDataFromButtonFilter(value.target.name);
          }}
        >
          New
        </SwitchButton>
        <SwitchButton
          name="MostPopular"
          bold={withFilter === "MostPopular"}
          onClick={(value: any) => {
            getDataFromButtonFilter(value.target.name);
          }}
        >
          Most Popular
        </SwitchButton>
        <SwitchButton
          name="OpenNow"
          bold={withFilter === "OpenNow"}
          onClick={(value: any) => {
            console.log(value);

            getDataFromButtonFilter(value.target.name);
          }}
        >
          Open Now
        </SwitchButton>
        <SwitchButton
          name="MapView"
          bold={withFilter === "MapView"}
          onClick={(value: any) => {
            getDataFromButtonFilter(value.target.name);
          }}
        >
          Map View
        </SwitchButton>
      </FilterButtonLine>
      <FilterRenegeLine>
        <FilterButtom text={"Price Range"} />
        {/* <FilterButtom text={"Distance"} />
        <FilterButtom text={"Rating"} /> */}
      </FilterRenegeLine>
      <GridCards repastCell={3}>
        {data.resturants.map((resturant, index) => {
          let chef = data.chef.find((chef) => chef.id === resturant.chefId);
          let name: string = chef?.firstName + " " + chef?.lastName;
          return (
            <CardResturant
              img={resturant.img}
              name={resturant.name}
              chef={name}
              rating={resturant.rating}
            />
          );
        })}
      </GridCards>
    </PageCenterStyle>
  );
};
export default ResturantsPage;
