import React, { ReactElement, useState } from "react";
import { Outlet } from "react-router-dom";
import FilterButtom, { SwitchButton } from "../../Styled_Components/Buttons";
import {
  FilterButtonLine,
  FilterRengeLine,
} from "../../Styled_Components/filterButtonLine";
import {
  GridCards,
  PageCenterStyle,
} from "../../Styled_Components/gridPagesStyles";
import data from "../../../data/resturants.json";
import CardResturant from "../../Cards/CardResturant/CardResturant";
import { useNavigate } from "react-router-dom";
const ResturantsPage = () => {
  const navigat = useNavigate();
  const [withFilter, setFilter] = useState("all");

  const getData = (state: string) => {
    setFilter(state);
    console.log(withFilter);
  };

  return (
    <PageCenterStyle>
      <FilterButtonLine>
        <SwitchButton
          name="all"
          bold={true}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          All
        </SwitchButton>
        <SwitchButton
          name="New"
          bold={withFilter === "New"}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          New
        </SwitchButton>
        <SwitchButton
          name="MostPopular"
          bold={withFilter === "MostPopular"}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          Most Popular
        </SwitchButton>
        <SwitchButton
          name="OpenNow"
          bold={withFilter === "OpenNow"}
          onClick={(value: any) => {
            getData(value.target.name);
          }}
        >
          Open Now
        </SwitchButton>
        <SwitchButton bold={withFilter === "MapView"}>Map View</SwitchButton>
      </FilterButtonLine>
      <FilterRengeLine>
        <FilterButtom text={"Price Range"} />
        <FilterButtom text={"Distance"} />
        <FilterButtom text={"Rating"} />
      </FilterRengeLine>
      <GridCards>
        {data.resturants.map((resturant, index) => {
          let chef = data.chef.find((chef) => chef.id === resturant.chefId);
          let name: string = chef?.firstName + " " + chef?.lastName;
          return (
            <CardResturant
              img={resturant.img}
              name={resturant.name}
              chef={name}
              rating={resturant.rating}
            />
          );
        })}
      </GridCards>
    </PageCenterStyle>
  );
};
export default ResturantsPage;

