import React, { ReactElement, useState } from "react";
import {
  FilterButtonLine,
  FilterRengeLine as FilterRenegeLine,
} from "../../Styled_Components/filterButtonLine";
import {
  GridCards,
  PageCenterStyle,
} from "../../Styled_Components/gridPagesStyles";
import data from "../../../data/resturants.json";
import CardResturant from "../../Cards/CardResturant/CardResturant";
import { SwitchButton } from "../../Styled_Components/Buttons";
import FilterButtom from "../../Styled_Components/FilterButtom";
import { useDispatch, useSelector } from "react-redux";
import { RootState } from "../../../data/store";
import IRestaurant from "../../Types/Interfaces/IRestaurant";
import { all, newRestaurants } from "../../../data/resturantSlicer";

const ResturantsPage = () => {
  const dispatch = useDispatch();
  const restaurants = useSelector(
    (state: RootState) => state.restaurants.value
  );
  const [withFilter, setFilter] = useState("all");
  const getDataFromButtonFilter = (state: string) => {
    setFilter(state);
  };
  return (
    <PageCenterStyle>
      <FilterButtonLine>
        <SwitchButton
          name="all"
          bold={withFilter === "all"}
          onClick={(value: any) => {
            dispatch(all());
            setFilter(value.target.name);
          }}
        >
          All
        </SwitchButton>
        <SwitchButton
          name="New"
          bold={withFilter === "New"}
          onClick={(value: any) => {
            dispatch(newRestaurants());
            setFilter(value.target.name);
          }}
        >
          New
        </SwitchButton>
        <SwitchButton
          name="MostPopular"
          bold={withFilter === "MostPopular"}
          onClick={(value: any) => {
            getDataFromButtonFilter(value.target.name);
          }}
        >
          Most Popular
        </SwitchButton>
        <SwitchButton
          name="OpenNow"
          bold={withFilter === "OpenNow"}
          onClick={(value: any) => {
            console.log(value);

            getDataFromButtonFilter(value.target.name);
          }}
        >
          Open Now
        </SwitchButton>
        <SwitchButton
          name="MapView"
          bold={withFilter === "MapView"}
          onClick={(value: any) => {
            getDataFromButtonFilter(value.target.name);
          }}
        >
          Map View
        </SwitchButton>
      </FilterButtonLine>
      <FilterRenegeLine>
        <FilterButtom text={"Price Range"} />
        <FilterButtom text={"Distance"} />
        <FilterButtom text={"Rating"} />
      </FilterRenegeLine>
      <GridCards repastCell={3}>
        {restaurants.map((restaurant: IRestaurant, index: number) => {
          let chef = data.chef.find((chef) => chef._id === restaurant.chefId);
          let name: string = chef?.firstName + " " + chef?.lastName;
          return (
            <CardResturant key={index} resturantData={restaurant} chef={name} />
          );
        })}
      </GridCards>
    </PageCenterStyle>
  );
};
export default ResturantsPage;
